import React from 'react'
import { AchievementMarker } from './AchievementMarker'
import { FilterDropdown } from './FilterDropdown'
import { GroupingToggle } from './GroupingToggle'

interface AppHeaderProps {
  isScrolled: boolean
  showControls: boolean
  setShowControls: (show: boolean) => void
  filters: {
    showAchievements: boolean
    categories: string[]
    countries: string[]
  }
  setFilters: (filters: any) => void
  groupingType: 'category' | 'country' | 'none'
  setGroupingType: (type: 'category' | 'country' | 'none') => void
  allCategories: string[]
  allCountries: string[]
  yearInputs: { start: string; end: string }
  setYearInputs: (inputs: { start: string; end: string }) => void
  applyYearFilter: (field: 'start' | 'end', value: string) => void
  handleYearKeyPress: (field: 'start' | 'end', e: React.KeyboardEvent<HTMLInputElement>) => void
  resetAllFilters: () => void
  getCategoryColor: (category: string) => string
}

export const AppHeader: React.FC<AppHeaderProps> = ({
  isScrolled,
  showControls,
  setShowControls,
  filters,
  setFilters,
  groupingType,
  setGroupingType,
  allCategories,
  allCountries,
  yearInputs,
  setYearInputs,
  applyYearFilter,
  handleYearKeyPress,
  resetAllFilters,
  getCategoryColor
}) => {
  return (
    <header 
      className={`app-header ${isScrolled ? 'scrolled' : ''}`}
      style={{
        padding: isScrolled ? '0.5rem 1rem' : '0.75rem 1rem',
        transition: 'all 0.3s ease'
      }}
    >
      {/* –û—Å–Ω–æ–≤–Ω–∞—è –ø–∞–Ω–µ–ª—å */}
      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between',
        alignItems: 'center',
        gap: '1rem',
        width: '100%',
        flexWrap: 'wrap'
      }}>
        {/* –ó–∞–≥–æ–ª–æ–≤–æ–∫ –∏ –∫–Ω–æ–ø–∫–∞ —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è */}
        <div style={{
          display: 'flex',
          alignItems: 'center',
          gap: '1rem',
          flexWrap: 'wrap'
        }}>
          <h1 style={{ 
            fontSize: isScrolled ? '1.5rem' : '1.8rem',
            margin: 0,
            transition: 'font-size 0.3s ease',
            fontWeight: 'bold',
            color: '#f4e4c1',
            flexShrink: 0
          }}>
            Chronoline
          </h1>
          
          {/* –ö–Ω–æ–ø–∫–∞ —Å–∫—Ä—ã—Ç–∏—è/–ø–æ–∫–∞–∑–∞ —ç–ª–µ–º–µ–Ω—Ç–æ–≤ —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è */}
          <button
            onClick={() => setShowControls(!showControls)}
            style={{
              padding: '0.3rem 0.6rem',
              background: 'rgba(139, 69, 19, 0.1)',
              border: '1px solid rgba(139, 69, 19, 0.3)',
              borderRadius: '4px',
              color: '#f4e4c1',
              fontSize: '0.7rem',
              cursor: 'pointer',
              transition: 'all 0.2s ease'
            }}
            title={showControls ? '–°–∫—Ä—ã—Ç—å —ç–ª–µ–º–µ–Ω—Ç—ã —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è' : '–ü–æ–∫–∞–∑–∞—Ç—å —ç–ª–µ–º–µ–Ω—Ç—ã —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è'}
          >
            {showControls ? '‚öôÔ∏è' : '‚öôÔ∏è'}
          </button>
        </div>

        {/* –ö–æ–º–ø–∞–∫—Ç–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã —É–ø—Ä–∞–≤–ª–µ–Ω–∏—è */}
        {showControls && (
          <div style={{
            display: 'flex',
            gap: '0.5rem',
            alignItems: 'center',
            flexWrap: 'wrap',
            justifyContent: 'center'
          }}>
            {/* –ü–µ—Ä–µ–∫–ª—é—á–∞—Ç–µ–ª—å –º–∞—Ä–∫–µ—Ä–æ–≤ */}
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '0.3rem',
              padding: '0.3rem 0.6rem',
              borderRadius: '6px',
              background: filters.showAchievements ? 'rgba(255, 255, 255, 0.1)' : 'rgba(255, 255, 255, 0.05)',
              border: `1px solid ${filters.showAchievements ? 'rgba(255, 255, 255, 0.2)' : 'rgba(255, 255, 255, 0.1)'}`,
              transition: 'all 0.2s ease',
              cursor: 'pointer'
            }}
            onClick={() => setFilters((prev: any) => ({ ...prev, showAchievements: !prev.showAchievements }))}
            title={filters.showAchievements ? '–°–∫—Ä—ã—Ç—å –º–∞—Ä–∫–µ—Ä—ã –¥–æ—Å—Ç–∏–∂–µ–Ω–∏–π' : '–ü–æ–∫–∞–∑–∞—Ç—å –º–∞—Ä–∫–µ—Ä—ã –¥–æ—Å—Ç–∏–∂–µ–Ω–∏–π'}
            >
              <AchievementMarker isActive={filters.showAchievements} />
            </div>

            {/* –ü–µ—Ä–µ–∫–ª—é—á–∞—Ç–µ–ª—å –≥—Ä—É–ø–ø–∏—Ä–æ–≤–∫–∏ */}
            <GroupingToggle 
              groupingType={groupingType}
              onGroupingChange={setGroupingType}
            />

            {/* –ö–æ–º–ø–∞–∫—Ç–Ω—ã–µ —Ñ–∏–ª—å—Ç—Ä—ã */}
            <div style={{
              display: 'flex',
              gap: '0.3rem',
              alignItems: 'center',
              flexWrap: 'wrap'
            }}>
              {/* –§–∏–ª—å—Ç—Ä –ø–æ –∫–∞—Ç–µ–≥–æ—Ä–∏—è–º */}
              <FilterDropdown
                title="üé≠"
                items={allCategories}
                selectedItems={filters.categories}
                onSelectionChange={(categories) => setFilters((prev: any) => ({ ...prev, categories }))}
                getItemColor={getCategoryColor}
                textLabel="–†–æ–¥ –¥–µ—è—Ç–µ–ª—å–Ω–æ—Å—Ç–∏"
              />
              
              {/* –§–∏–ª—å—Ç—Ä –ø–æ —Å—Ç—Ä–∞–Ω–∞–º */}
              <FilterDropdown
                title="üåç"
                items={allCountries}
                selectedItems={filters.countries}
                onSelectionChange={(countries) => setFilters((prev: any) => ({ ...prev, countries }))}
                textLabel="–°—Ç—Ä–∞–Ω–∞"
              />
              
              {/* –§–∏–ª—å—Ç—Ä –ø–æ –≥–æ–¥–∞–º */}
              <div style={{
                display: 'flex',
                gap: '0.2rem',
                alignItems: 'center'
              }}>
                <input
                  type="number"
                  value={yearInputs.start}
                  onChange={(e) => setYearInputs((prev: any) => ({ ...prev, start: e.target.value }))}
                  onBlur={(e) => applyYearFilter('start', e.target.value)}
                  onKeyPress={(e) => handleYearKeyPress('start', e)}
                  placeholder="–û—Ç"
                  style={{
                    width: '50px',
                    padding: '0.2rem 0.3rem',
                    border: '1px solid rgba(139, 69, 19, 0.3)',
                    borderRadius: '3px',
                    background: 'rgba(44, 24, 16, 0.8)',
                    color: '#f4e4c1',
                    fontSize: '0.6rem',
                    textAlign: 'center'
                  }}
                />
                <span style={{ fontSize: '0.6rem', color: '#f4e4c1' }}>-</span>
                <input
                  type="number"
                  value={yearInputs.end}
                  onChange={(e) => setYearInputs((prev: any) => ({ ...prev, end: e.target.value }))}
                  onBlur={(e) => applyYearFilter('end', e.target.value)}
                  onKeyPress={(e) => handleYearKeyPress('end', e)}
                  placeholder="–î–æ"
                  style={{
                    width: '50px',
                    padding: '0.2rem 0.3rem',
                    border: '1px solid rgba(139, 69, 19, 0.3)',
                    borderRadius: '3px',
                    background: 'rgba(44, 24, 16, 0.8)',
                    color: '#f4e4c1',
                    fontSize: '0.6rem',
                    textAlign: 'center'
                  }}
                />
              </div>
              
              {/* –ö–Ω–æ–ø–∫–∞ —Å–±—Ä–æ—Å–∞ */}
              {(filters.categories.length > 0 || filters.countries.length > 0 || 
                yearInputs.start !== '-800' || yearInputs.end !== '2000') && (
                <button
                  onClick={resetAllFilters}
                  style={{
                    padding: '0.2rem 0.4rem',
                    background: 'rgba(231, 76, 60, 0.2)',
                    border: '1px solid rgba(231, 76, 60, 0.4)',
                    borderRadius: '3px',
                    color: '#e74c3c',
                    fontSize: '0.6rem',
                    cursor: 'pointer',
                    transition: 'all 0.2s ease'
                  }}
                  title="–°–±—Ä–æ—Å–∏—Ç—å –≤—Å–µ —Ñ–∏–ª—å—Ç—Ä—ã"
                >
                  ‚úï
                </button>
              )}
            </div>
          </div>
        )}
      </div>
    </header>
  )
}
