import React from 'react'
import { AchievementMarker } from './AchievementMarker'
import { FilterDropdown } from './FilterDropdown'
import { GroupingToggle } from './GroupingToggle'
import { Logo } from './Logo'
import { YearRangeSlider } from './YearRangeSlider'

interface AppHeaderProps {
  isScrolled: boolean
  showControls: boolean
  setShowControls: (show: boolean) => void
  filters: {
    showAchievements: boolean
    hideEmptyCenturies: boolean
    categories: string[]
    countries: string[]
  }
  setFilters: (filters: any) => void
  groupingType: 'category' | 'country' | 'none'
  setGroupingType: (type: 'category' | 'country' | 'none') => void
  allCategories: string[]
  allCountries: string[]
  yearInputs: { start: string; end: string }
  setYearInputs: (inputs: { start: string; end: string } | ((prev: { start: string; end: string }) => { start: string; end: string })) => void
  applyYearFilter: (field: 'start' | 'end', value: string) => void
  handleYearKeyPress: (field: 'start' | 'end', e: React.KeyboardEvent<HTMLInputElement>) => void
  resetAllFilters: () => void
  getCategoryColor: (category: string) => string
  sortedData: any[]
  handleSliderMouseDown: (e: React.MouseEvent | React.TouchEvent, handle: 'start' | 'end') => void
  isDraggingSlider: boolean
}

export const AppHeader: React.FC<AppHeaderProps> = ({
  isScrolled,
  showControls,
  setShowControls,
  filters,
  setFilters,
  groupingType,
  setGroupingType,
  allCategories,
  allCountries,
  yearInputs,
  setYearInputs,
  applyYearFilter,
  handleYearKeyPress,
  resetAllFilters,
  getCategoryColor,
  sortedData,
  handleSliderMouseDown,
  isDraggingSlider
}) => {


  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏—è —Ñ–∏–ª—å—Ç—Ä–∞ "–ü–æ–∫–∞–∑–∞—Ç—å –≤—Å–µ –≤–µ–∫–∞" —Å —Å–±—Ä–æ—Å–æ–º –¥–∞—Ç
  const handleHideEmptyCenturiesToggle = () => {
    const newHideEmptyCenturies = !filters.hideEmptyCenturies;

    // –ï—Å–ª–∏ –≤–∫–ª—é—á–∞–µ–º "–ü–æ–∫–∞–∑–∞—Ç—å –≤—Å–µ –≤–µ–∫–∞" (hideEmptyCenturies = false), —Å–±—Ä–∞—Å—ã–≤–∞–µ–º –¥–∏–∞–ø–∞–∑–æ–Ω –¥–∞—Ç
    if (!newHideEmptyCenturies) {
      setYearInputs({ start: '-800', end: '2000' });
      applyYearFilter('start', '-800');
      applyYearFilter('end', '2000');
    }

    setFilters((prev: any) => ({ ...prev, hideEmptyCenturies: newHideEmptyCenturies }));
  };



  return (
    <header 
      className={`app-header ${isScrolled ? 'scrolled' : ''}`}
      id="app-header"
      role="banner"
              aria-label="–ó–∞–≥–æ–ª–æ–≤–æ–∫ –ø—Ä–∏–ª–æ–∂–µ–Ω–∏—è Chrono Ninja"
      style={{
        padding: isScrolled ? '0.5rem 1rem' : '0.75rem 1rem',
        transition: 'all 0.3s ease'
      }}
    >
      <div className="header-row" role="navigation" aria-label="–û—Å–Ω–æ–≤–Ω–∞—è –Ω–∞–≤–∏–≥–∞—Ü–∏—è">
        <h1 className="header-title" id="app-title" style={{ 
          fontSize: isScrolled ? '1.5rem' : '1.8rem',
          margin: 0,
          transition: 'font-size 0.3s ease',
          fontWeight: 'bold',
          color: '#f4e4c1',
          flexShrink: 0
        }}>
          <Logo />
        </h1>

        {/* –ö–Ω–æ–ø–∫–∏ "–ü–æ–¥–µ–ª–∏—Ç—å—Å—è" - –≤—Ä–µ–º–µ–Ω–Ω–æ —Å–∫—Ä—ã—Ç—ã */}
        <div className="share-buttons" style={{
          display: 'none', // –í—Ä–µ–º–µ–Ω–Ω–æ —Å–∫—Ä—ã—Ç—ã
          gap: '0.5rem',
          alignItems: 'center',
          marginLeft: 'auto',
          marginRight: '1rem'
        }}>
          <button
            onClick={() => {
              if (navigator.share) {
                navigator.share({
                  title: 'Chrono Ninja - –í—Ä–µ–º–µ–Ω–Ω—ã–µ –ª–∏–Ω–∏–∏ –∏—Å—Ç–æ—Ä–∏—á–µ—Å–∫–∏—Ö –ª–∏—á–Ω–æ—Å—Ç–µ–π',
                  text: '–ò–∑—É—á–∞–π—Ç–µ –±–∏–æ–≥—Ä–∞—Ñ–∏–∏ –∏ –¥–æ—Å—Ç–∏–∂–µ–Ω–∏—è –≤—ã–¥–∞—é—â–∏—Ö—Å—è –ª—é–¥–µ–π —Ä–∞–∑–Ω—ã—Ö —ç–ø–æ—Ö',
                  url: window.location.href
                });
              } else {
                // Fallback –¥–ª—è –±—Ä–∞—É–∑–µ—Ä–æ–≤ –±–µ–∑ Web Share API
                navigator.clipboard.writeText(window.location.href);
                alert('–°—Å—ã–ª–∫–∞ —Å–∫–æ–ø–∏—Ä–æ–≤–∞–Ω–∞ –≤ –±—É—Ñ–µ—Ä –æ–±–º–µ–Ω–∞!');
              }
            }}
            style={{
              padding: '0.4rem 0.6rem',
              background: 'rgba(52, 152, 219, 0.2)',
              border: '1px solid rgba(52, 152, 219, 0.4)',
              borderRadius: '4px',
              color: '#3498db',
              fontSize: '0.7rem',
              cursor: 'pointer',
              transition: 'all 0.2s ease',
              display: 'flex',
              alignItems: 'center',
              gap: '0.3rem'
            }}
            title="–ü–æ–¥–µ–ª–∏—Ç—å—Å—è"
            aria-label="–ü–æ–¥–µ–ª–∏—Ç—å—Å—è —Å—Å—ã–ª–∫–æ–π –Ω–∞ –ø—Ä–∏–ª–æ–∂–µ–Ω–∏–µ"
          >
            üì§ –ü–æ–¥–µ–ª–∏—Ç—å—Å—è
          </button>

          <button
            onClick={() => {
              const url = `https://twitter.com/intent/tweet?text=${encodeURIComponent('–ò–∑—É—á–∞—é –∏—Å—Ç–æ—Ä–∏—á–µ—Å–∫–∏–µ –ª–∏—á–Ω–æ—Å—Ç–∏ —Å Chrono Ninja!')}&url=${encodeURIComponent(window.location.href)}`;
              window.open(url, '_blank');
            }}
            style={{
              padding: '0.4rem 0.6rem',
              background: 'rgba(29, 161, 242, 0.2)',
              border: '1px solid rgba(29, 161, 242, 0.4)',
              borderRadius: '4px',
              color: '#1da1f2',
              fontSize: '0.7rem',
              cursor: 'pointer',
              transition: 'all 0.2s ease'
            }}
            title="–ü–æ–¥–µ–ª–∏—Ç—å—Å—è –≤ Twitter"
            aria-label="–ü–æ–¥–µ–ª–∏—Ç—å—Å—è –≤ Twitter"
          >
            üê¶
          </button>

          <button
            onClick={() => {
              const url = `https://www.facebook.com/sharer/sharer.php?u=${encodeURIComponent(window.location.href)}`;
              window.open(url, '_blank');
            }}
            style={{
              padding: '0.4rem 0.6rem',
              background: 'rgba(66, 103, 178, 0.2)',
              border: '1px solid rgba(66, 103, 178, 0.4)',
              borderRadius: '4px',
              color: '#4267b2',
              fontSize: '0.7rem',
              cursor: 'pointer',
              transition: 'all 0.2s ease'
            }}
