                }}
                onKeyDown={(e) => {
                  if (e.key === 'ArrowLeft' || e.key === 'ArrowRight') {
                    e.preventDefault()
                    const currentStart = parseYearValue(yearInputs.start, -800)
                    const currentEnd = parseYearValue(yearInputs.end, 2000)
                    const step = e.shiftKey ? 100 : 10
                    let newEnd = currentEnd

                    if (e.key === 'ArrowLeft') {
                      newEnd = Math.max(currentStart + 100, currentEnd - step)
                    } else {
                      newEnd = Math.min(2000, currentEnd + step)
                    }

                    setYearInputs(prev => ({ ...prev, end: newEnd.toString() }))
                    applyYearFilter('end', newEnd.toString())
                  }
                }}
                                 style={{
                   position: 'absolute',
                   top: '-2px',
                   left: `${Math.max(0, Math.min(100, ((parseYearValue(yearInputs.end, 2000) + 800) / 2800 * 100)))}%`,
                   width: '12px',
                   height: '12px',
                   background: '#daa520',
                   border: '2px solid #f4e4c1',
                   borderRadius: '50%',
                   cursor: 'grab',
                   transform: 'translateX(-50%)',
                   transition: isDraggingSlider ? 'none' : 'all 0.2s ease',
                   zIndex: 2
                 }}
              />
            </div>
          </div>

          {/* –ö–Ω–æ–ø–∫–∞ —Å–±—Ä–æ—Å–∞ - –æ—Ç–¥–µ–ª—å–Ω–æ */}
          <div style={{ 
            display: 'flex', 
            justifyContent: 'center', 
            width: '100%',
            opacity: (filters.categories.length > 0 || filters.countries.length > 0 || yearInputs.start !== '-800' || yearInputs.end !== '2000') ? 1 : 0,
            pointerEvents: (filters.categories.length > 0 || filters.countries.length > 0 || yearInputs.start !== '-800' || yearInputs.end !== '2000') ? 'auto' : 'none',
            transition: 'opacity 0.2s ease'
          }}>
            <button
              onClick={resetAllFilters}
              style={{
                padding: '0.4rem 0.6rem',
                background: 'rgba(231, 76, 60, 0.2)',
                border: '1px solid rgba(231, 76, 60, 0.4)',
                borderRadius: '4px',
                color: '#e74c3c',
                fontSize: '0.7rem',
                cursor: 'pointer',
                transition: 'all 0.2s ease'
              }}
              title="–°–±—Ä–æ—Å–∏—Ç—å –≤—Å–µ —Ñ–∏–ª—å—Ç—Ä—ã"
            >
              ‚úï
            </button>
          </div>
        </div>
      </div>
    </header>
  )
}
