import { useState, useRef, useCallback, useEffect } from 'react'

interface UseTimelineDragProps {
  timelineWidth: number
  containerWidth: number
}

export const useTimelineDrag = ({ timelineWidth, containerWidth }: UseTimelineDragProps) => {
  const [isDragging, setIsDragging] = useState(false)
  const [dragStartX, setDragStartX] = useState(0)
  const [dragStartY, setDragStartY] = useState(0)
  const [scrollLeft, setScrollLeft] = useState(0)
  const [scrollTop, setScrollTop] = useState(0)
  const [isDraggingTimeline, setIsDraggingTimeline] = useState(false)
  const timelineRef = useRef<HTMLDivElement>(null)
  const dragStartTime = useRef<number>(0)

  const handleMouseDown = useCallback((e: React.MouseEvent) => {
    const target = e.target as HTMLElement
    const isInteractive = target.closest('.life-bar') || 
                         target.closest('.achievement-marker') ||
                         target.closest('button') ||
                         target.closest('[role="button"]')

    if (isInteractive) return

    setIsDragging(true)
    setDragStartX(e.clientX)
    setScrollLeft(timelineRef.current?.scrollLeft || 0)
    dragStartTime.current = Date.now()
    e.preventDefault()
  }, [])

  const handleMouseMove = useCallback((e: React.MouseEvent) => {
    if (!isDragging) return

    const deltaX = e.clientX - dragStartX
    const newScrollLeft = scrollLeft - deltaX

    if (timelineRef.current) {
      timelineRef.current.scrollLeft = Math.max(0, newScrollLeft)
    }
  }, [isDragging, dragStartX, scrollLeft])

  const handleMouseUp = useCallback(() => {
    if (!isDragging) return

    const dragDuration = Date.now() - dragStartTime.current

    if (dragDuration < 200) {
      setIsDraggingTimeline(false)
    } else {
      setIsDraggingTimeline(true)
      setTimeout(() => setIsDraggingTimeline(false), 100)
    }

    setIsDragging(false)
  }, [isDragging])

  const handleTouchStart = useCallback((e: React.TouchEvent) => {
    const target = e.target as HTMLElement
    const isInteractive = target.closest('.life-bar') || 
                         target.closest('.achievement-marker') ||
                         target.closest('button') ||
                         target.closest('[role="button"]')

    if (isInteractive) return

    setIsDragging(true)
    setDragStartX(e.touches[0].clientX)
    setScrollLeft(timelineRef.current?.scrollLeft || 0)
    dragStartTime.current = Date.now()
  }, [])

  const handleTouchMove = useCallback((e: React.TouchEvent) => {
    if (!isDragging) return

    const deltaX = e.touches[0].clientX - dragStartX
    const newScrollLeft = scrollLeft - deltaX

    if (timelineRef.current) {
      timelineRef.current.scrollLeft = Math.max(0, newScrollLeft)
    }
  }, [isDragging, dragStartX, scrollLeft])

  const handleTouchEnd = useCallback(() => {
    if (!isDragging) return

    const dragDuration = Date.now() - dragStartTime.current

    if (dragDuration < 200) {
      setIsDraggingTimeline(false)
    } else {
      setIsDraggingTimeline(true)
      setTimeout(() => setIsDraggingTimeline(false), 100)
    }

    setIsDragging(false)
  }, [isDragging])

  useEffect(() => {
    return () => {
      setIsDragging(false)
      setIsDraggingTimeline(false)
    }
  }, [])

  return {
    timelineRef,
    isDragging,
    isDraggingTimeline,
    handleMouseDown,
    handleMouseMove,
    handleMouseUp,
    handleTouchStart,
    handleTouchMove,
    handleTouchEnd
  }
}
