import React, { useRef, useEffect, useState } from 'react'

interface FilterDropdownProps {
  title: string
  items: string[]
  selectedItems: string[]
  onSelectionChange: (items: string[]) => void
  getItemColor?: (item: string) => string
  icon?: React.ReactNode
  textLabel?: string
}

export const FilterDropdown: React.FC<FilterDropdownProps> = ({ 
  title, 
  items, 
  selectedItems, 
  onSelectionChange, 
  getItemColor,
  icon,
  textLabel
}) => {
  const isActive = selectedItems.length > 0
  const dropdownRef = useRef<HTMLDivElement>(null)
  const contentRef = useRef<HTMLDivElement>(null)
  const [dropdownPosition, setDropdownPosition] = useState<'bottom' | 'top'>('bottom')
  const [isOpen, setIsOpen] = useState(false)
  const [isMobile, setIsMobile] = useState(false)
  
  // Определяем, является ли устройство мобильным
  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768)
    }
    
    checkMobile()
    window.addEventListener('resize', checkMobile)
    return () => window.removeEventListener('resize', checkMobile)
  }, [])
  
  // Функция для определения позиции dropdown
  const updateDropdownPosition = () => {
    if (!dropdownRef.current || !contentRef.current) return
    
    const dropdownRect = dropdownRef.current.getBoundingClientRect()
    const viewportHeight = window.innerHeight
    const viewportWidth = window.innerWidth
    
    // Оценка высоты контента (примерная)
    const estimatedContentHeight = Math.min(items.length * 30 + 80, 300) // 30px на элемент + 80px для кнопок
    
    // Проверяем, помещается ли dropdown снизу
    const spaceBelow = viewportHeight - dropdownRect.bottom
    const spaceAbove = dropdownRect.top
    
    // Проверяем горизонтальное позиционирование
    const contentWidth = 250 // примерная ширина dropdown
    const spaceRight = viewportWidth - dropdownRect.left
    const spaceLeft = dropdownRect.right
    
    if (spaceBelow < estimatedContentHeight && spaceAbove > estimatedContentHeight) {
      setDropdownPosition('top')
    } else {
      setDropdownPosition('bottom')
    }
  }
  
  // Обработчики для разных устройств
  const handleMouseEnter = () => {
    if (!isMobile) {
      setIsOpen(true)
      updateDropdownPosition()
    }
  }
  
  const handleMouseLeave = () => {
    if (!isMobile) {
      setIsOpen(false)
    }
  }
  
  const handleClick = () => {
    if (isMobile) {
      setIsOpen(!isOpen)
      if (!isOpen) {
        updateDropdownPosition()
      }
    }
  }
  
  // Обработка клика вне dropdown для мобильных устройств
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (isMobile && dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setIsOpen(false)
      }
    }
    
    if (isMobile && isOpen) {
      document.addEventListener('mousedown', handleClickOutside)
    }
    
    return () => {
      document.removeEventListener('mousedown', handleClickOutside)
    }
  }, [isMobile, isOpen])
  
  return (
    <div 
      className="filter-dropdown" 
      ref={dropdownRef}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <button 
        className={`filter-btn ${isActive ? 'active' : ''}`}
        style={{ 
          minWidth: 'auto',
          padding: '0.3rem 0.6rem',
          display: 'flex', 
          alignItems: 'center', 
          gap: '0.3rem',
          background: isActive ? 'rgba(139, 69, 19, 0.15)' : 'rgba(139, 69, 19, 0.08)',
          border: `1px solid ${isActive ? 'rgba(139, 69, 19, 0.3)' : 'rgba(139, 69, 19, 0.2)'}`,
          borderRadius: '4px',
          color: '#f4e4c1',
          fontSize: '0.7rem',
          cursor: 'pointer',
          transition: 'all 0.2s ease',
          whiteSpace: 'nowrap'
        }}
        onClick={handleClick}
      >
        {icon && <span className="filter-icon">{icon}</span>}
        <span className="filter-text">{textLabel || title}</span>
        {isActive && <span className="filter-count">({selectedItems.length})</span>}
      </button>
      {isOpen && (
        <div 
          className="filter-dropdown-content"
          ref={contentRef}
          style={{
            position: 'absolute',
            top: dropdownPosition === 'bottom' ? '100%' : 'auto',
            bottom: dropdownPosition === 'top' ? '100%' : 'auto',
            left: '0',
            right: '0',
            zIndex: 1000,
            maxHeight: '300px',
            overflowY: 'auto',
            display: 'block'
          }}
        >
          <div style={{ padding: '0.5rem', borderBottom: '1px solid rgba(139, 69, 19, 0.3)' }}>
            <button
              onClick={(e) => {
                e.stopPropagation()
                onSelectionChange(items)
              }}
              style={{
                padding: '0.25rem 0.5rem',
                marginRight: '0.5rem',
                background: '#27ae60',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '0.7rem'
              }}
            >
              Выбрать все
            </button>
            <button
              onClick={(e) => {
                e.stopPropagation()
                onSelectionChange([])
              }}
              style={{
                padding: '0.25rem 0.5rem',
                background: '#e74c3c',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
                fontSize: '0.7rem'
              }}
            >
              Снять все
            </button>
          </div>
          {items.map(item => (
            <label key={item} className="filter-checkbox" onClick={(e) => e.stopPropagation()}>
              <input
                type="checkbox"
                checked={selectedItems.includes(item)}
                onChange={(e) => {
                  e.stopPropagation()
                  if (e.target.checked) {
                    onSelectionChange([...selectedItems, item])
                  } else {
                    onSelectionChange(selectedItems.filter(i => i !== item))
                  }
                }}
              />
              <span style={{ 
                color: getItemColor ? getItemColor(item) : '#f4e4c1',
                fontWeight: getItemColor ? 'bold' : 'normal'
              }}>
                {item}
              </span>
            </label>
          ))}
        </div>
      )}
    </div>
  )
}
